mapper.select:
select CONCAT(menu_name,'(', GROUP_CONCAT(code_text), ')') from sys_role_menu rm join sys_menu m on rm.menu_id=m.id_ join sys_dic d on rm.permission_=d.code_ join sys_dic_index di on d.index_id=di.id_ and di.key_='CRUD'
rm.role_id=#{roleId}
group by menu_name order by menu_id

select CONCAT(menu_id, permission_) from sys_role_menu
role_id=#{roleId}
order by menu_id
