mapper.delete:

delete from sys_user_menu
where user_id = #{userId,jdbcType=VARCHAR}
  and menu_id = #{menuId,jdbcType=VARCHAR}
  and permission_ = #{permission,jdbcType=VARCHAR}

mapper.insert:

insert into sys_user_menu (user_id, menu_id, permission_, 
  enable_, remark_, create_by, 
  create_time, update_by, update_time
  )
values (#{userId,jdbcType=VARCHAR}, #{menuId,jdbcType=VARCHAR}, #{permission,jdbcType=VARCHAR}, 
  #{enable,jdbcType=BIT}, #{remark,jdbcType=VARCHAR}, #{createBy,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.update:

update sys_user_menu
set enable_ = #{enable,jdbcType=BIT},
  remark_ = #{remark,jdbcType=VARCHAR},
  create_by = #{createBy,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  update_by = #{updateBy,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=VARCHAR}
  and menu_id = #{menuId,jdbcType=VARCHAR}
  and permission_ = #{permission,jdbcType=VARCHAR}

mapper.select:

select user_id, menu_id, permission_, enable_, remark_, create_by, create_time, update_by, 
update_time
from sys_user_menu
where user_id = #{userId,jdbcType=VARCHAR}
  and menu_id = #{menuId,jdbcType=VARCHAR}
  and permission_ = #{permission,jdbcType=VARCHAR}
